mapper.select:

select BNO, TITLE, CONTENT, WRITE_DATE, NICKNAME
from (
	select
		rownum rn, b.BNO, b.TITLE, b.CONTENT, b.WRITE_DATE, m.NICKNAME
	from tbl_board b left join tbl_member m on b.WRITER = m.EMAIL
	where 



	rownum <= #{pageNo} * #{pageSize}
	order by bno desc
	)
where rn > (#{pageNo}-1) * #{pageSize}

select BNO, TITLE, CONTENT, WRITE_DATE, MODIFY_DATE, LIKE_CNT, HATE_CNT, WRITER
from tbl_board
where bno = #{bno}

mapper.update:
update tbl_board
set title = #{title}, content = #{content}, MODIFY_DATE = sysdate
where bno = #{bno}

update tbl_board
set LIKE_CNT = LIKE_CNT + 1
where bno = #{bno}

update tbl_board
set HATE_CNT = HATE_CNT + 1
where bno = #{bno}

update tbl_board
set VIEW_CNT = VIEW_CNT + 1
where bno = #{bno}
